mapper.select:
select h.id, h.name, h.phone, h.telephone, h.address, h.enabled, h.userface, h.username, h.remark, r.id role_id, r.name role_name, r.nameZh role_nameZh from hr h left join hr_role hr on h.id = hr.hrid left join role r on r.id = hr.rid where h.id != #{id}
and h.name like concat('%', #{keywords}, '%')
order by h.id

mapper.update:
update hr
set password = #{encodePass}
where id = #{id}
